   if (IS_NOT_NULL(einfo)) einfo->par = (UChar* )NULL;

   if (ci->pattern_enc != ci->target_enc) {
    r = conv_encoding(ci->pattern_enc, ci->target_enc, pattern, pattern_end,
                      &cpat, &cpat_end);
